function get_svg($name)
{
    global $svgs;
    return $svgs[$name] ?? '';
}

// Shortcode function to display all SVGs and their slugs
function display_svgs_shortcode()
{
    global $svgs;
    $output = '<div class="svgs-container">';
    foreach ($svgs as $slug => $svg) {
        $output .= '<div class="svg-item">';
        $output .= '<div class="svg">' . $svg . '</div>';
        $output .= '<div class="slug">' . esc_html($slug) . '</div>';
        $output .= '</div>';
    }
    $output .= '</div>';

    return $output;
}

// Register the shortcode
add_shortcode('load-svgs', 'display_svgs_shortcode');

